    "import streamlit as st\n",
    "import pandas as pd\n",
    "import joblib\n",
    "from pulp import LpProblem, LpVariable, lpSum, LpMaximize, LpBinary\n",
    "import numpy as np\n",
    "import plotly.graph_objects as go\n",
    "import plotly.express as px\n",
